element.addEventListener('plusChange', onChange);

import { Element } from '@htmlplus/element';
import { Browse } from "TODO";

const BrowseDefault = () => {
  const onChange = event => {
    const name = event.detail.files[0].file.name;
    alert(`File '${name}' selected.`);
  };

  return <>    
    <Browse droppable onChange={event => onChange(event)}></Browse>    
  </>;
};

export default BrowseDefault;

import { Element } from '@htmlplus/element';
export default {
  methods: {
    onChange(event) {
      const name = event.detail.files[0].file.name;
      alert(`File '${name}' selected.`);
    }

  }
};

import { html } from "@htmlplus/element/runtime";
import { Element } from '@htmlplus/element';

@Element("plus-browse-default")
@Element("plus-browse-default")
class BrowseDefault {
  static members = {};

  onChange(event) {
    const name = event.detail.files[0].file.name;
    alert(`File '${name}' selected.`);
  }

  render() {
    return html`<plus-browse droppable onPlusChange=${event => this.onChange(event)}></plus-browse>`;
  }

}

