import { Element } from '@htmlplus/element';

@Element()
class BrowseDefault {
  onChange(event) {
    const name = event.detail.files[0].file.name;
    alert(`File '${name}' selected.`);
  }
  render() {
    return (
      <plus-browse
        droppable
        onPlusChange={(event) => this.onChange(event)}
