```tsx [script]
import { Element, State } from '@htmlplus/element';

@Element()
class CardElevation {
  @State()
  elevation = "12";

  onChange(event) {
    this.elevation = event.target.value;
  }

  render() {
    return (
      <fragment>
        <plus-card elevation={this.elevation}></plus-card>
        <br />
        <input
          type="range"
          value={this.elevation}
          min="1"
          max="24"
          onChange={(event) => this.onChange(event)}
        />
      </fragment>
    );
  }
}
```

