```tsx [script]
import { Element, State } from '@htmlplus/element';

@Element()
class DialogSize {
  @State()
  open = false;

  @State()
  size = 'md';

  hide() {
    this.open = false;
  }

  show(size) {
    this.size = size;
    this.open = true;
  }

  render() {
    return (
      <fragment>
        <plus-grid justify-content="center" gutter="md">
          <plus-grid-item xs="12" sm="auto">
            <button onClick={() => this.show('sm')}>Small dialog</button>
          </plus-grid-item>
          <plus-grid-item xs="12" sm="auto">
            <button onClick={() => this.show('lg')}>Large dialog</button>
          </plus-grid-item>
          <plus-grid-item xs="12" sm="auto">
            <button onClick={() => this.show('xl')}>Extra large dialog</button>
          </plus-grid-item>
        </plus-grid>
        <plus-dialog
          animation="fade"
          open={this.open}
          size={this.size}
          onPlusClose={() => this.hide()}
        >
          <plus-dialog-content>
            <plus-dialog-header>Dialog Title</plus-dialog-header>
            <plus-dialog-body>
              Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do
              eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut
              enim ad minim veniam, quis nostrud exercitation ullamco laboris
              nisi ut aliquip ex ea commodo consequat.
            </plus-dialog-body>
            <plus-dialog-footer>
              <button onClick={() => this.hide()}>Close</button>
            </plus-dialog-footer>
          </plus-dialog-content>
        </plus-dialog>
      </fragment>
    );
  }
}
